package polymorphism;

public class Add {

	public void add(int b) {
		// TODO Auto-generated method stub
		System.out.println("first number"+b);
		
	}

	public void add(int b, int c) {
		// TODO Auto-generated method stub
		System.out.println("add 2 number:"+(b+c));
	}

	public void add(int b, int c, int d) {
		// TODO Auto-generated method stub
		System.out.println("add 3 numbers:"+(b+c+d));
	}
	
}
